mapper.select:
SELECT * FROM fitness_class


        AND type = #{category}


        AND (name LIKE CONCAT('%', #{search}, '%') OR instructor LIKE CONCAT('%', #{search}, '%') OR location LIKE CONCAT('%', #{search}, '%'))


ORDER BY created_at DESC
LIMIT #{start}, #{pageSize}

mapper.update:
UPDATE fitness_class
name = #{name}, type = #{type}, instructor = #{instructor}, time = #{time}, location = #{location}, capacity = #{capacity}, status = #{status}, updated_at = #{updatedAt}
WHERE class_id = #{class_id}
